/**
 * Generated by MqttPanelCraft
 */

#include <Arduino.h>
#include "mqttpanel.h"

// --- Platform Specifics ---
#if defined(ESP32)
  #include <WiFi.h>
#elif defined(ESP8266)
  #include <ESP8266WiFi.h>
#endif

// --- User Config ---
// If LittleFS has saved config, these defaults might be overwritten.
char mqtt_server[40] = "{{BROKER}}";
char mqtt_port[6]    = "{{PORT}}";
char mqtt_topic[40]  = "{{BASE_TOPIC}}/"; // Ensure trailing slash

// Button Settings (Sec)
#define BTN_PORTAL_SEC 3
#define BTN_RESET_SEC  10
#define CHECK_WIFI_SEC 20

// Hardware Pins
#define TRIGGER_PIN 0  // Boot Button
#define LED_PIN     4  // Status LED

// --- Objects ---
WiFiClient espClient;
PubSubClient client(espClient);

// --- Component Globals ---

base.setup_start:
void mq_receiver(String topic, String msg);

void setup() {
  // 1. Hardware Init
  Serial.begin(115200);
  Serial.println("\n[System] Booting...");
  pinMode(TRIGGER_PIN, INPUT_PULLUP);
  pinMode(LED_PIN, OUTPUT);

  // 2. Start MQTT Panel
  mqttpanel_begin(&client, mq_receiver, 
           mqtt_server, mqtt_port, mqtt_topic,
           BTN_PORTAL_SEC, BTN_RESET_SEC,
           TRIGGER_PIN, LED_PIN,
           CHECK_WIFI_SEC);

  Serial.println("[Main] Configured:");
  Serial.printf(" - Server: %s\n", mqtt_server);
  Serial.printf(" - Topic:  %s\n", mqtt_topic);

  // 3. Subscriptions (Generated)

BUTTON_CTRL.receiver_logic:
if (topic == String(mqtt_topic) + "{{REL_TOPIC_SET}}") {
  sw_{{INDEX}} = (msg == "1");
  Serial.println("Button {{INDEX}} Pressed: " + String(sw_{{INDEX}}));
  // TODO: Action
}

SWITCH_CTRL.receiver_logic:
if (topic == String(mqtt_topic) + "{{REL_TOPIC_SET}}") {
  sw_{{INDEX}} = (msg == "1");
  Serial.println("Set Switch {{INDEX}} to " + String(sw_{{INDEX}}));
  // TODO: digitalWrite(PIN_{{INDEX}}, sw_{{INDEX}});
  // mqttpanel_pub(String(mqtt_topic) + "{{REL_TOPIC_VAL}}", sw_{{INDEX}}?"1":"0"); // Ack
}

DIMMER_CTRL.receiver_logic:
if (topic == String(mqtt_topic) + "{{REL_TOPIC_SET}}") {
  dim_{{INDEX}} = msg.toInt();
  Serial.println("Set Dimmer {{INDEX}} to " + String(dim_{{INDEX}}));
  // TODO: analogWrite(PIN_{{INDEX}}, dim_{{INDEX}});
}

NUMBER_VAL.loop_logic:
static unsigned long last_num_{{INDEX}} = 0;
if (millis() - last_num_{{INDEX}} > 5000) {
  last_num_{{INDEX}} = millis();
  // num_{{INDEX}} = random(200, 300) / 10.0; // Mock Data
  mqttpanel_pub(String(mqtt_topic) + "{{REL_TOPIC_VAL}}", String(num_{{INDEX}}));
}
